ement.appendChild(trElement);\\r\\n\\t\\t\\t\\t\\t\\r\\n\\t\\t\\t\\t\\ttableElement.appendChild(tbodyElement);\\r\\n\\t\\t\\t\\t\\t\\r\\n\\t\\t\\t\\t\\taElement.onclick=function(){\\r\\n\\t\\t\\t\\t\\t\\t//使网页链接取消\\r\\n\\t\\t\\t\\t\\t\\treturn delTr(aElement);\\r\\n\\t\\t\\t\\t\\t}\\r\\n\\t\\t\\t\\t}\\r\\n\\t\\t\\t\\t\\r\\n\\t\\t\\t}\\r\\n\\t\\t\\t/**\\r\\n\\t\\t\\t * \\r\\n\\t\\t\\t * @param {Object} aElement\\r\\n\\t\\t\\t */\\r\\n\\t\\t\\tfunction delTr(aElement){\\r\\n\\t\\t\\t\\tvar name = aElement.parentNode.parentNode.firstChild.firstChild.nodeValue;\\r\\n\\t\\t\\t\\t\\r\\n\\t\\t\\t\\tvar flag = window.confirm(&quot;你真的要删除[&quot;+name+&quot;]这个用户吗？&quot;);\\r\\n\\t\\t\\t\\tif(!flag){\\r\\n\\t\\t\\t\\t\\treturn false;\\r\\n\\t\\t\\t\\t}\\r\\n\\t\\t\\t\\tvar tbodyElement = aElement.parentNode.parentNode.parentNode;\\r\\n\\t\\t\\t\\tvar trElement =aElement.parentNode.parentNode;\\r\\n\\t\\t\\t\\ttbodyElement.removeChild(trElement);\\r\\n\\t\\t\\t\\treturn false;\\r\\n\\t\\t\\t}\\r\\n\\t\\t\\t\\r\\n\\t\\t&lt;/script&gt;\\r\\n\\t\\t\\r\\n\\t&lt;/head&gt;\\r\\n\\t&lt;body&gt;\\r\\n\\t\\t&lt;center&gt;\\r\\n\\t\\t\\t添加用户：\\r\\n\\r\\n\\t\\t\\t姓名：&lt;input type=&quot;text&quot; name=&quot;name&quot; id=&quot;name&quot;/&gt;\\r\\n\\t\\t\\t邮箱：&lt;input type=&quot;text&quot; name=&quot;email&quot; id=&quot;email&quot;/&gt;\\r\\n\\t\\t\\t电话：&lt;input type=&quot;text&quot; name=&quot;tel&quot; id=&quot;tel&quot;/&gt;\\r\\n\\t\\t\\t&lt;button id=&quot;addUser&quot;&gt;提交&lt;/button&gt;\\r\\n\\t\\t\\t&lt;hr&gt;&lt;table border=1 width=&quot;600px&quot; id=&quot;userTable&quot;&gt;\\r\\n\\t\\t\\t\\t&lt;tr&gt;\\r\\n\\t\\t\\t\\t\\t&lt;th&gt;姓名&lt;/th&gt;&lt;th&gt;邮箱&lt;/th&gt;&lt;th&gt;电话&lt;/th&gt;&lt;th&gt;管理&lt;/th&gt;\\r\\n\\t\\t\\t\\t&lt;/tr&gt;\\r\\n\\t\\t\\t\\t\\r\\n\\t\\t\\t&lt;/table&gt;\\r\\n\\t\\t&lt;/center&gt;\\r\\n\\t&lt;/body&gt;\\r\\n&lt;/html&gt;\\r\\n</textarea></p>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\"],\"summary\":[\"\"],\"id\":[\"\"],\"tokenKey\":[\"88109c8c-6b0