&quot;input[type=text][name=birthday]&quot;).val());&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;//alert($(&#39;:input[type=checkbox]&#39;).size());&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;//alert($(&#39;:radio[name=sex]&#39;).last().val());&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;alert($(&#39;form&nbsp;:selected&#39;).get(0));//返回原生对象&nbsp;&nbsp;</li>\\r\\n\\t<li>});&nbsp;&nbsp;</li>\\r\\n</ol>\\r\\n\\r\\n<p>&nbsp;</p>\\r\\n\\r\\n<p>Javascript代码(do.js)</p>\\r\\n\\r\\n<ol start=\\\"1\\\">\\r\\n\\t<li>function&nbsp;loadData(jsonStr){&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;key,value,tagName,type,arr;&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;for(x&nbsp;in&nbsp;jsonStr){&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;key&nbsp;=&nbsp;x;&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;value&nbsp;=&nbsp;jsonStr[x];&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$(&quot;[name=&#39;&quot;+key+&quot;&#39;],[name=&#39;&quot;+key+&quot;[]&#39;]&quot;).each(function(){&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;tagName&nbsp;=&nbsp;$(this)[0].tagName;&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;type&nbsp;=&nbsp;$(this).attr(&#39;type&#39;);&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(tagName==&#39;INPUT&#39;){&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(type==&#39;radio&#39;){&nbsp;</li>\\r\\n\\t<li>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if($(this).val()==value)</li>\\r\\n\\t<li>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $(this).attr(&#39;checked&#39;,true);&nbsp;&nbsp;