bsp;+&nbsp;&quot;\\\\\\\\&quot;&nbsp;+&nbsp;name&nbsp;+&nbsp;&quot;.class&quot;;&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;try&nbsp;{&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;FileInputStream&nbsp;fis&nbsp;=&nbsp;new&nbsp;FileInputStream(classFileName);&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;ByteArrayOutputStream&nbsp;bos&nbsp;=&nbsp;new&nbsp;ByteArrayOutputStream();&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cypher(fis,&nbsp;bos);&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fis.close();&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;byte[]&nbsp;bytes&nbsp;=&nbsp;bos.toByteArray();&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;defineClass(bytes,&nbsp;0,&nbsp;bytes.length);&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;catch&nbsp;(Exception&nbsp;e)&nbsp;{&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//&nbsp;TODO&nbsp;Auto-generated&nbsp;catch&nbsp;block&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;e.printStackTrace();&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;super.findClass(name);&nbsp;&nbsp;</li>\\r\\n\\t<li>&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;</li>\\r\\n</ol>\\r\\n\"],\"summary\":[\"\"],\"id\":[\"8\"],\"tokenKey\":[\"966e6c4d-ae04-4e46-9de5-81b31f8aacb9\"],\"title\":[\"类加载器\"],\"tokenValue\":[\"5046f8d3-3a17-411e-a542-e487c074ffa3\"],\"cid\":[\"4\"],\"typeId\":[\"13\"]}','2018-02-27 14:55:26',0),(9250,'127.0.0.1',2,'管理员','','article/13/4','{\"tokenKey\":[\"de8026b8-20d3-4640-bce6-a438187635b7\"],\"pageNo\":[\"1\"],\"pag